import React, { useEffect, useState }  from "react";
import { ArtContainer, ArtContent, RandomPiece } from "./PageElements";
import Loader from 'react-loader-spinner';
const Art = () => {

  const [isLoading, setIsLoading] = useState(false);
  const [isImageLoaded, setIsImageLoaded] = useState(false);
  const [randomArt, setRandomArt] = useState();
  const [randomArtName, setRandomArtName] = useState('loading...');

  const artScreen = () => {
    return(
      <>
      <RandomPiece src={randomArt} alt="Rando Art Randomizing..." className={isImageLoaded ? 'visible' : 'hidden'}
      onLoad={() => setIsImageLoaded(true)}/>
        <p>{randomArtName}</p>
      </>
    );
  }

  const getArt = async () => {
    await fetch('https://secret-basin-38348.herokuapp.com/random')
      .then((response) => response.json())
      .then((data) => {setRandomArt(data.url); setRandomArtName(data.name)})
      .catch((error) => console.log(error.message));
  };

  useEffect( () => {
    setIsLoading(true);
    getArt().then(r => {setIsLoading(false)});

    console.log(randomArt);
  }, []);


  return (
    <ArtContainer>
      <ArtContent>
        <h1>Random Piece Generator</h1>
        {isLoading ?
          <Loader type="Circles" color="#00BFFF" height={80} width={80}/> :
          artScreen()}

      </ArtContent>
    </ArtContainer>
  );
};

export default Art;
