import React, { useEffect, useState }  from "react";
import { ArtContainer, ArtContent, RandomPiece } from "./PageElements";
import Loader from 'react-loader-spinner';
const Art = () => {

  const [IsLoading, setIsLoading] = useState(false);
  const [randomArt, setRandomArt] = useState();
  const [randomArtName, setRandomArtName] = useState('loading...');

  const getArt = async () => {
    await fetch('https://secret-basin-38348.herokuapp.com/random')
      .then((response) => response.json())
      .then((data) => {setRandomArt(data.url); setRandomArtName(data.name)})
      .catch((error) => console.log(error.message));
  };

  useEffect( () => {
    setIsLoading(true);
    getArt().then(r => {setIsLoading(false)});

    console.log(randomArt);
  }, []);


  return (
    <ArtContainer>
      <ArtContent>
        <h1>Random Piece Generator</h1>
        <Loader type="Circles" color="#00BFFF" height={80} width={80}/>
        <RandomPiece src={randomArt} alt="Rando Art Randomizing..."/>
        <p>{randomArtName}</p>
      </ArtContent>
    </ArtContainer>
  );
};

export default Art;
