import React, { useEffect, useState }  from "react";
import { ArtContainer, ArtContent, RandomPiece, PieceContainer, ArtButton} from "./PageElements";
import Loader from "../components/Loader";
import ConLoader from "../components/Loader";
const Art = () => {

  const [isLoading, setIsLoading] = useState(false);
  const [isImageLoaded, setIsImageLoaded] = useState(false);
  const [randomArt, setRandomArt] = useState();
  const [randomArtName, setRandomArtName] = useState();

  const ArtTitle = () => {
    return(
      <p style={{fontStyle: 'italic'}}><b>{randomArtName}</b></p>);
  }

  const ArtButtons = () => {
    return(<><ArtButton onClick={() => {setIsImageLoaded(false); setIsLoading(true); fetchArt().then(r => {setIsLoading(false)});}}>See another random piece</ArtButton>
    <ArtButton>See all</ArtButton>
      <ArtButton>Check Out the API</ArtButton>
    </>);
  }
  const ArtScreen = () => {
    return(

      <>
      <RandomPiece src={randomArt} alt="Rando Art Randomizing..." style={isImageLoaded ? {} : { display: 'none' }}
                   onLoad={() => setIsImageLoaded(true)}/>
                   <br/>
        {isImageLoaded && <ArtButtons/>}
        </>

    );
  }

  const fetchArt = async () => {
    await fetch('https://secret-basin-38348.herokuapp.com/random')
      .then((response) => response.json())
      .then((data) => {setRandomArt(data.url); setRandomArtName(data.name)})
      .catch((error) => console.log(error.message));
  };

  useEffect( () => {
    setIsLoading(true);
    fetchArt()
      .then(r => {setIsLoading(false)});
  }, []);


  return (
    <ArtContainer>
      <ArtContent>
        <h1>Random Piece Generator
        </h1>
        <PieceContainer>
          <br/>
          {!isImageLoaded && <ConLoader/>}
        {!isLoading && <ArtScreen/>}
          {isImageLoaded ? <ArtTitle/> : <p>fetching...</p>}

        </PieceContainer>

      </ArtContent>
    </ArtContainer>
  );
};

export default Art;
