import React, { useEffect, useState }  from "react";
import { ArtContainer, ArtContent, RandomPiece, PieceContainer, ArtButton} from "./PageElements";
import Loader from 'react-loader-spinner';
const Art = () => {

  const [isLoading, setIsLoading] = useState(false);
  const [isImageLoaded, setIsImageLoaded] = useState(false);
  const [randomArt, setRandomArt] = useState();
  const [randomArtName, setRandomArtName] = useState();

  const ArtScreen = () => {
    return(

      <RandomPiece src={randomArt} alt="Rando Art Randomizing..." style={{visible: isImageLoaded}}
                   onLoad={() => setIsImageLoaded(true)}/>


    );
  }

  const getArt = async () => {
    await fetch('https://secret-basin-38348.herokuapp.com/random')
      .then((response) => response.json())
      .then((data) => {setRandomArt(data.url); setRandomArtName(data.name)})
      .catch((error) => console.log(error.message));
  };

  useEffect( () => {
    setIsLoading(true);
    getArt().then(r => {setIsLoading(false)});

    console.log(randomArt);
  }, []);


  return (
    <ArtContainer>
      <ArtContent>
        <h1>Random Piece Generator
        </h1>
        <PieceContainer>
          <ArtButton>See another random piece</ArtButton>
          <ArtButton>See all</ArtButton>
          <br/>
          {!isImageLoaded && <Loader type="Circles" color="#00BFFF" height={80} width={80} style={{paddingTop: '10%'}}/>
          }
        {!isLoading && <ArtScreen/>}
          {isImageLoaded ? <p>{randomArtName}</p> : <p>fetching...</p>}

        </PieceContainer>

      </ArtContent>
    </ArtContainer>
  );
};

export default Art;
